import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API_URL from './constants'; // Import your API URL file
import './AuthPage.css';

const AuthPage = () => {
  const navigate = useNavigate();
  const [signupFormData, setSignupFormData] = useState({
    Name: '',
    ContactId: '',
    Email: '',
    Address: '',
    ZipCode: '',
    UserName: '',
    Password: '',
  });

  const [loginFormData, setLoginFormData] = useState({
    UserName: '',
    Password: '',
  });

  const handleSignupInputChange = (e) => {
    setSignupFormData({
      ...signupFormData,
      [e.target.name]: e.target.value,
    });
  };

  const handleLoginInputChange = (e) => {
    setLoginFormData({
      ...loginFormData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSignup = async () => {
    try {
      const response = await fetch(`${API_URL}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(signupFormData),
      });

      const data = await response.json();

      if (data.issuccess) {
        // Successful signup, navigate to the '/update-track' route
        console.log('Signup successful');
        navigate('/track'); // Update the route as needed
      } else {
        console.error('Signup failed:', data.error);
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  const handleLogin = async () => {
    try {
      const response = await fetch(`${API_URL}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginFormData),
      });

      const data = await response.json();

      if (data.userId) {
        // Successful login, navigate to another page or perform an action
        console.log('Login successful');
        navigate('/track'); // Change '/dashboard' to your desired route
      } else {
        console.error('Login failed:', data.error);
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>Signup</h2>
        {/* ... Signup input fields */}
        <button onClick={handleSignup}>Signup</button>
      </div>

      <div className="auth-form">
        <h2>Login</h2>
        {/* ... Login input fields */}
        <button onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
};

export default AuthPage;
