// CTOData.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import API_URL from './constants';  // Import the API_URL constant
import './CTOData.css';

const CTOData = () => {
  const [ctoData, setCTOData] = useState([]);
  const [editedData, setEditedData] = useState({});

  useEffect(() => {
    // Fetch CTO data from the backend when the component mounts
    axios.post(`${API_URL}/track`, { /* Add any required parameters */ })
      .then(response => {
        // Check if the response.data has a 'data' key and if it's an array
        if (response.data && response.data.data && Array.isArray(response.data.data)) {
          setCTOData(response.data.data);
        } else {
          console.error('Invalid data structure in API response:', response.data);
        }
      })
      .catch(error => console.error('Error fetching CTO data', error));
  }, []);
  

  const handleEdit = (field, value, index) => {
    // Update the editedData state when a field is edited
    setEditedData({
      ...editedData,
      [index]: {
        ...editedData[index],
        [field]: value,
      },
    });
  };

  const handleSave = () => {
    // Send the edited data to the backend to update the database
    axios.post('http://localhost:5000/update', editedData)
      .then(response => {
        console.log('Update successful:', response.data);
        // Optionally, update the local state or show a success message
      })
      .catch(error => console.error('Error updating data', error));
  };

  return (
    <div>
      <h2>CTO Data</h2>
      <table>
        <thead>
          {/* Add table headers based on your CTO data structure */}
        </thead>
        <tbody>
          {ctoData.map((row, index) => (
            <tr key={index}>
              {/* Display each field in a table cell */}
              <td>{row.field1}</td>
              <td>{row.field2}</td>
              {/* Add more cells for other fields */}
              <td>
                {/* Editable input for a specific field */}
                <input
                  type="text"
                  value={editedData[index]?.field1 || row.field1}
                  onChange={(e) => handleEdit('field1', e.target.value, index)}
                />
              </td>
              {/* Add more cells for other editable fields */}
            </tr>
          ))}
        </tbody>
      </table>
      <button onClick={handleSave}>Save Changes</button>
    </div>
  );
};

export default CTOData;
