import React, { useState } from 'react';
import axios from 'axios';
import './TrackAndUpdateComponent.css'; // Include your CSS file
import API_URL from './constants';

function TrackAndUpdateComponent() {
  const [piValue, setPIValue] = useState('');
  const [irbValue, setIRBValue] = useState(0);
  const [formValues, setFormValues] = useState({});
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    try {
      const response = await axios.post(`${API_URL}/track`, {
        PI: piValue,
        IRB: irbValue,
      });

      setFormValues(response.data);
      setError('');
    } catch (error) {
      setError('Error fetching data. Please try again.');
    }
  };

  return (
    <div className="track-container">
      <div className="track-form">
        <h2>Track and Update</h2>
        <label htmlFor="piInput">PI:</label>
        <input
          type="text"
          id="piInput"
          value={piValue}
          onChange={(e) => setPIValue(e.target.value)}
        />

        <label htmlFor="irbInput">IRB:</label>
        <input
          type="number"
          id="irbInput"
          value={irbValue}
          onChange={(e) => setIRBValue(parseInt(e.target.value, 10))}
        />

        <button onClick={handleSubmit}>Submit</button>
        {error && <p className="error">{error}</p>}
      </div>

      {Object.keys(formValues).length > 0 && (
        <div className="result-container">
          <h2>Form Values:</h2>
          <table>
            <tbody>
              {Object.entries(formValues).map(([key, value]) => (
                <tr key={key}>
                  <td>{key}:</td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>
          {/* Add update fields and button here */}
        </div>
      )}
    </div>
  );
}

export default TrackAndUpdateComponent;
